import { useState } from 'react';
import React from 'react';
function App() {

  let [title, _title] = useState(['ÎÇ®Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú', 'ÎßõÏßë Ï∂îÏ≤ú', 'ÏòÅÌôî Ï∂îÏ≤ú'])
  var [cnt, c] = useState(0);
  let [modal, _modal] = useState(false);//UIÎ≥¥ÏûÑ ÏïàÎ≥¥ÏûÑÏùÑ stateÎ°ú Ï†ÄÏû•Ìï¥Îë† Ï§ëÏöîÌïú Ï†ïÎ≥¥Îäî stateÎ°ú Ï†ÄÏû•
  var [num, _num] = useState(0);
  let [inputValue, _inputValue] = useState('');

  return (
    <div className="App">
      <div className="black-nav">
        <div>Í∞úÎ∞ú Blog</div>
      </div>
      {
        title.map(function (t, i) {//map Î∞òÎ≥µÎ¨∏ÏúºÎ°ú ÎèåÎ¶∞ htmlÏóêÎäî key ÌïÑÏöî
          return (
            < div className="list" key={i} >
              <h2 onClick={() => { _num(i) }}> {t}
                <span onClick={() => { c(cnt + 1); }}>üëç</span> {cnt}
              </h2>
              <p>2Ïõî 19Ïùº Î∞úÌñâ</p>
              <hr />
            </div>
          )
        })
      }
      {/* <input onChange={(e) => { _inputValue(e.target.value) }}></input> */}
      <div className="publish">
        <input onChange={(e) => {
          _inputValue(e.target.value)
        }} />
        <button onClick={() => {
          var newTitle = [...title];// spraied operator?
          newTitle.unshift(inputValue);
          _title(newTitle);
        }}>Ï†ÄÏû•</button>
      </div>
      <button onClick={() => { _modal(!modal) }}>Ïó¥Í≥† Îã´Í∏∞</button>
      {
        modal ? <Modal title={title} num={num}></Modal> : null
      }

    </div >
  );
}

function Modal(props) {//ÏÉàÎ°úÏö¥ component ÎßåÎìúÎäîÎ≤ï
  return (
    <div className="modal">
      <h2>{props.title[props.num]}</h2>
      <p>ÎÇ†Ïßú</p>
      <p>ÏÉÅÏÑ∏ÎÇ¥Ïö©</p>
    </div>
  )
}
export default App;

